import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import DataTable from "react-data-table-component";
import './App.css';
import "bootstrap-icons/font/bootstrap-icons.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import StockDetailPage from './StockDetailPage';
import { Link } from 'react-router-dom';

const Home = () => {
  const [stockdata, setStockdata] = useState([]);
  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/")
      .then((response) => {
        setStockdata(response.data);
        console.log(stockdata);
      })
      .catch((err) => {
        alert(err);
      })
  })
  const columns = [
    {
      name: "Id",
      selector: row => row.id,
      sortable: true,
      width: "80px",
      ca
    },
    {
      name: "Date",
      selector: row => row.date,
      sortable: true
    },
    {
      name: "Trade Code",
      selector: row => row.trade_code,
      sortable: true,
      width: "120px",

    },
    {
      name: "High",
      selector: row => row.high,
      sortable: true
    },
    {
      name: "Low",
      selector: row => row.low,
      sortable: true
    },
    {
      name: "Open",
      selector: row => row.open,
      sortable: true
    },
    {
      name: "Close",
      selector: row => row.close,
      sortable: true
    },
    {
      name: "Volume",
      selector: row => row.volume,
      sortable: true
    },
    {
      name: "Action",
      selector: "null",
      sortable: false,
      width: '80px',
      cell: (e) => [
        <Link to={`/${e.id}`}><i className="bi bi-pen-fill" ></i></Link>,
        <Link onClick={()=> handleDelete(e.id)}><i className="bi bi-trash-fill" style={{color :'red', marginLeft: '10px'}}></i></Link>
      ]
    }

  ];
  const handleDelete = async (id) => {
    try {
        const response = await axios.delete(`http://127.0.0.1:8000/${id}/`, {
            headers: {
                "Content-Type": "application/json",
            },
        });
        alert("Stock deleted successfully!");       
    } catch (err) {
        alert(err);
    }
};
  return (
    <div className="App">
      <h2 className="App-header">Stock Market Data</h2>
      <div className="container">
        <DataTable columns={columns} data={stockdata} pagination />
      </div>
    </div>
  );
}
const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/:id" element={<StockDetailPage />} />
      </Routes>
    </Router>
  );

}

export default App;
