import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import "bootstrap/dist/css/bootstrap.min.css";
const StockDetailPage = () => {
    const { id } = useParams(); // Get the id from the URL
    const [stockDetails, setStockDetails] = useState(null);

    useEffect(() => {
        axios
            .get(`http://127.0.0.1:8000/${id}`)
            .then((response) => {
                setStockDetails(response.data);
            })
            .catch((err) => {
                alert(err);
            });
    }, [id]);

    const handleEdit = async (e) => {
        e.preventDefault(); // Prevent form from refreshing the page
    
        console.log("Submitting data:", stockDetails); // Log data to console
    
        try {
            const response = await axios.put(`http://127.0.0.1:8000/${id}/`, stockDetails, {
                headers: {
                    "Content-Type": "application/json",
                },
            });
            console.log("Response from server:", response.data); // Log response from backend
            alert("Stock details updated successfully!");
            setStockDetails(response.data);
        } catch (err) {
            console.error("Error updating stock details:", err.response ? err.response.data : err);
            alert("Error updating stock details");
        }
    };
    
    const handleChange = (e) => {
        setStockDetails({
            ...stockDetails,
            [e.target.name]: e.target.value,
            
        });
    };

    return (
        <div className="App">
            <h2 className='App-header'>Stock Details for ID: {id}</h2>
            {stockDetails ? (
                <div className='container'>
                    {/* <table className='table'>            
          <tr><th>Date</th> <td>{stockDetails.date}</td></tr>
          <tr><th>Trade Code</th> <td> {stockDetails.trade_code}</td></tr>
          <tr><th>High</th> <td>{stockDetails.high}</td></tr>
          <tr><th>Low </th> <td>{stockDetails.low}</td></tr>
          <tr><th>Open</th> <td> {stockDetails.open}</td></tr>
          <tr><th>Close </th> <td>{stockDetails.close}</td></tr>
          <tr><th>Volume </th> <td>{stockDetails.volume}</td></tr>
        </table> */}
        <form className='detail_form' onSubmit={handleEdit}>
            <label>Date</label><input name='date' value={stockDetails.date} onChange={handleChange} /> <br />
            <label>Trade Code</label><input name='trade_code' value={stockDetails.trade_code} onChange={handleChange} /> <br />
            <label>High</label><input name='high' value={stockDetails.high} onChange={handleChange} /> <br />
            <label>Low</label><input name='low' value={stockDetails.low} onChange={handleChange} /> <br />
            <label>Open</label><input name='open' value={stockDetails.open} onChange={handleChange} /> <br />
            <label>Close</label><input name='close' value={stockDetails.close} onChange={handleChange} /> <br />
            <label>Volume</label><input name='volume' value={stockDetails.volume} onChange={handleChange} /> <br />
            <button type='submit'>Save</button>
        </form>


    </div>
    ) : (
        <p>Loading...</p>
    )}
</div>
);
};

export default StockDetailPage;
