import './App.css';
import axios from 'axios';
import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import DataTable from 'datatables.net-react';
import DT from 'datatables.net-dt';

DataTable.use(DT);
function App() {
  const table_header = ["date","trade_code","high","low","open","close","volume"];
  const [data, setData] = useState([]);
  useEffect(()=>{
    axios
    .get("http://127.0.0.1:8000/stock/")
    .then((res)=>{
      setData(res.data);
    })
    .catch((err) => {
      console.error(err);
    })
  },[]);
  const handleInputChange = (event, index, key) => {
    const updatedData = [...data];
    updatedData[index][key] = event.target.value;
    setData(updatedData);
  };
  const handleSave = async () => {
    try {
      for (const row of data) {
        // Assuming the API allows PUT requests for updating individual records
        await axios.put(`http://127.0.0.1:8000/stock/${row.id}/`, row);
      }
      alert("Data saved successfully!");
    } catch (error) {
      console.error("Error saving data:", error);
      alert("Failed to save data.");
    }
  };
  return (
    <div className="App">
      <h2 className='App-header'>Stock Market Data</h2>
      <div className='container'>
      <DataTable>
        <thead>
          <tr>
            {table_header.map((item, index)=>(
                <th key={index}>{item}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((detail, id)=>(
            <tr key={id}>
              {table_header.map((value, id2)=>(
                <td key={id2}><input type='text' value={detail[value]} onChange={(e)=>handleInputChange(e,id2,value)}/></td>
              ))}
            </tr>
          ))}
        </tbody>
      </DataTable>

    </div>
    <button className='btn btn-success' onClick={handleSave}>Save</button>
    </div>
  );
}

export default App;
