import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import DataTable from "react-data-table-component";
import './App.css';



const App1 = () => {
  const tableHeader = ["date", "trade_code", "high", "low", "open", "close", "volume"];
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/stock/")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  // Handle changes to the input fields
  const handleInputChange = (e, rowIndex, fieldName) => {
    const updatedData = [...data];
    updatedData[rowIndex] = { ...updatedData[rowIndex], [fieldName]: e.target.value };
    setData(updatedData);
  };

  // Handle the save operation for each row
  const handleSave = (id, updatedData) => {
    axios
      .put(`http://127.0.0.1:8000/stock/${id}/`, updatedData)
      .then(() => {
        alert("Data updated successfully!");
      })
      .catch((err) => {
        alert("Error updating data: " + err);
      });
  };

  const columns = tableHeader.map((field) => ({
    name: field.toUpperCase(),
    selector: (row) => row[field],
    sortable: true,
    cell: (row, rowIndex) => (
      <input
        type="text"
        value={row[field]}
        onChange={(e) => handleInputChange(e, rowIndex, field)}
        style={{ width: "90px", fontSize:"15px" }}  
      />
    ),
  }));

  return (
    <div className="App">
      <h2 className="App-header">Stock Market Data</h2>
      <div className="container">
        <DataTable
          columns={columns}
          data={data}
          pagination
          highlightOnHover
          dense
        />
      </div>

      {/* Render Save Button for each row */}
      <div className="mt-3">
        {data.map((row, index) => (
          <div key={row.id} className="d-flex justify-content-between">
            <button
              className="btn btn-success"
              onClick={() => handleSave(row.id, row)} // Pass row's id and updated data
            >
              Save
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};


export default App;
