import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import DataTable from "react-data-table-component";
import './App.css';

const App = () => {
  const tableHeader = ["date", "trade_code", "high", "low", "open", "close", "volume"];
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/stock/")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);


  const handleInputChange = (e, rowIndex, fieldName) => {
    const updatedData = [...data];
    updatedData[rowIndex] = { ...updatedData[rowIndex], [fieldName]: e.target.value };
    setData(updatedData);
  };


  const handleSave = () => {
    axios
      .post("http://127.0.0.1:8000/stock/update", data)
      .then(() => {
        alert("Data updated successfully!");
      })
      .catch((err) => {
        alert(err);
      });
  };


  const columns = tableHeader.map((field) => ({
    name: field.toUpperCase().bold(),
    selector: (row) => row[field],
    sortable: true,
    cell: (row, rowIndex) => (
      <input
        type="text"
        value={row[field]}
        onChange={(e) => handleInputChange(e, rowIndex, field)}
        className="form-control"
      />
    ),
  }));

  return (
    <div className="App">
      <h2 className="App-header">Stock Market Data</h2>
      <div className="container">
        <DataTable
          columns={columns}
          data={data}
          pagination
          highlightOnHover
          dense

        />
      </div>
      <button className="btn btn-success" onClick={handleSave}>
        Save
      </button>
    </div>
  );
};

export default App;
