import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import DataTable from "react-data-table-component";
import './App.css';
import "bootstrap-icons/font/bootstrap-icons.css";
import {BrowserRouter as Router, Routes, Route} from "react-router-dom";
import StockDetailPage from './StockDetailPage';
import {Link} from 'react-router-dom';
const App1 = () => {
  const tableHeader = ["date", "trade_code", "high", "low", "open", "close", "volume"];
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/stock/")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  // Handle changes to the input fields
  const handleInputChange = (e, rowIndex, fieldName) => {
    const updatedData = [...data];
    updatedData[rowIndex] = { ...updatedData[rowIndex], [fieldName]: e.target.value };
    setData(updatedData);
  };

  // Handle the save operation for each row


  const columns = tableHeader.map((field) => ({
    name: field.toUpperCase(),
    selector: (row) => row[field],
    sortable: true,
    cell: (row, rowIndex) => (
      <input
        type="text"
        value={row[field]}
        onChange={(e) => handleInputChange(e, rowIndex, field)}
        style={{ width: "90px", fontSize:"15px" }}  
      />
    ),
  }));

  return (
    <div className="App">
      <h2 className="App-header">Stock Market Data</h2>
      <div className="container">
        <DataTable
          columns={columns}
          data={data}
          pagination
          highlightOnHover
          dense
        />
      </div>

      {/* Render Save Button for each row */}
      <div className="mt-3">
        {data.map((row, index) => (
          <div key={row.id} className="d-flex justify-content-between">
            <button
              className="btn btn-success"
              onClick={() => handleSave(row.id, row)} // Pass row's id and updated data
            >
              Save
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
const App2 = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get("http://127.0.0.1:8000/stock/")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  // Function to handle edit button click
  const handleEdit = (row) => {
    console.log("Editing row:", row);
    // Implement your edit functionality here
  };

  // Function to handle delete button click
  const handleDelete = (row) => {
    console.log("Deleting row:", row);
    // Implement your delete functionality here
  };

  // Define columns with action buttons
  const columns = [
    { name: "Date", selector: "date", sortable: true },
    { name: "Trade Code", selector: "trade_code", sortable: true },
    { name: "High", selector: "high", sortable: true },
    { name: "Low", selector: "low", sortable: true },
    { name: "Open", selector: "open", sortable: true },
    { name: "Close", selector: "close", sortable: true },
    { name: "Volume", selector: "volume", sortable: true },
    {
      name: "Actions",
      cell: (row) => (
        <div>
          <button onClick={() => handleEdit(row)} className="btn btn-primary btn-sm">
            Edit
          </button>
          <button onClick={() => handleDelete(row)} className="btn btn-danger btn-sm" style={{ marginLeft: "5px" }}>
            Delete
          </button>
        </div>
      ),
      ignoreRowClick: true,
      allowOverflow: true,
      button: true,
    },
  ];

  return (
    <div className="App">
      <h2>Stock Market Data</h2>
      <DataTable columns={columns} data={data} pagination highlightOnHover />
    </div>
  );
}
const App3 = () => {
  const tableHeader = ["date", "trade_code", "high", "low", "open", "close", "volume"];
  const [stockdata, setStockdata] = useState([]);
  useEffect(()=>{
    axios
    .get("http://127.0.0.1:8000/")
    .then((response)=>{
      setStockdata(response.data);
      console.log(stockdata);
    })
    .catch((err)=>{
      alert(err);
    })
})
  const columns = [
    {
      name: "Id",
      selector: row=>row.id,
      sortable: true,
      width: "80px",
    },
    {
      name: "Date",
      selector: row => row.date,
      sortable: true 
    },
    {
      name: "Trade Code",
      selector: row => row.trade_code,
      sortable: true,
      width: "120px",

    },
    {
      name: "High",
      selector: row => row.high,
      sortable: true
    },
    {
      name: "Low",
      selector: row=>row.low,
      sortable: true
    },
    {
      name: "Open",
      selector: row => row.open,
      sortable: true
    },
    {
      name: "Close",
      selector: row => row.close,
      sortable: true
    },
    {
      name: "Volume",
      selector: row => row.volume,
      sortable: true
    },
    {
      name: "Action",
      selector: "null",
      sortable: false,
      width: '80px',
      cell: (e)=>[
        <Link to ={`/${e.id}`}><i className="bi bi-pen-fill" ></i></Link>      
      ]      
    }

  ]; 

  const handleedit = (id) =>{
      axios
      .put("http://127.0.0.1:8000/" + id)
      .then((response)=>{
        setStockdata(response.data)
      })
      .catch((err)=>{
        alert(err);
      })
  }

  return(  
    <div className="App">
      <h2 className="App-header">Stock Market Data</h2>  
      <div className="container">
      <DataTable columns={columns} data={stockdata} pagination/>
      </div>      
    </div>    
  );
}
const App = () => {
  return (
    <Router>
      <Routes>
      <Route path="/" element={<App3/>}/>
      <Route path="/:id" element={<StockDetailPage />} />
      </Routes>
    </Router>
    // <App3/>
  );

}

export default App;
